import React from "react";
import CssBaseline from "@material-ui/core/CssBaseline";
import Typography from "@material-ui/core/Typography";
import { makeStyles } from "@material-ui/core/styles";
import Container from "@material-ui/core/Container";
import Link from "@material-ui/core/Link";
import FacebookIcon from '@material-ui/icons/Facebook';
import Button from '@material-ui/core/Button'
import { Link as RouterLink, useHistory } from 'react-router-dom'
import InstagramIcon from '@material-ui/icons/Instagram';
function Copyright() {
  return (
    <Typography variant="body2" color="white">
      {"Copyright © "}
      <Link color="inherit" href="./">
        Your Website
      </Link>{" "}
      {new Date().getFullYear()}
      {"."}
    </Typography>
  );
}

const useStyles = makeStyles((theme) => ({
  root: {
    display: "flex",
    flexDirection: "column",
    minHeight: "80vh",

  },
  main: {
    marginTop: theme.spacing(8),
    marginBottom: theme.spacing(2),
  },
  footer: {
    padding: theme.spacing(3, 2),
    marginTop: "auto",
    backgroundColor:
    "rgba(0, 0, 0, 0.94)",
    color:"white",
    height:"260px"


  },
  web:{
    fontSize:30,

  },
  face:{
    marginBottom: theme.spacing(30),

  },
  contact:{
    marginLeft:theme.spacing(200)
  }
}));

export default function StickyFooter() {
  const classes = useStyles();

  return (
    <div className={classes.root}>
      <CssBaseline />
      <Container
        component="main"
        className={classes.main}
        maxWidth="sm"
      ></Container>
      <footer className={classes.footer}>
        <Container maxWidth="sm">
          <Typography variant="body1" className={classes.web}> Bakery Web Site</Typography>
          <Copyright />
          <Button  href="https://www.facebook.com/GoodDay-Bakery-639001883403275/" color="inherit">
           Follow us : <FacebookIcon/>
           <Button  href="https://www.instagram.com/goodday_bakery1/?igshid=7usjhae6u5cb&fbclid=IwAR1loYSCvnTmiaytha_B59cxspSeyrwPXkLopm1C3XG-MBS6g_GgStEkMks" color="inherit">
           <InstagramIcon/> 
          </Button> 
          </Button>
        </Container>
        <Container maxWidth="sm"><Typography variant="body1" className={classes.contact}> Bakery Web Site</Typography> </Container>
      </footer>
    </div>
  );
}
