import React from "react";
import PropTypes from "prop-types";
import { withStyles } from "@material-ui/core/styles";
import Grid from "@material-ui/core/Grid";
import Container from "@material-ui/core/Container";
import Button from "@material-ui/core/Button";
import Typography from "./Typography";
import Radio from "@material-ui/core/Radio";
import RadioGroup from "@material-ui/core/RadioGroup";
import FormControlLabel from "@material-ui/core/FormControlLabel";
import FormControl from "@material-ui/core/FormControl";
import FormLabel from "@material-ui/core/FormLabel";

const styles = (theme) => ({
  root: {
    display: "flex",
    backgroundColor: "hsla(0, 100%, 70%, 0.1)",
    overflow: "hidden",
  },
  container: {
    marginTop: theme.spacing(10),
    marginBottom: theme.spacing(15),
    position: "relative",
    display: "flex",
    flexDirection: "column",
    alignItems: "center",
  },
  item: {
    display: "flex",
    flexDirection: "column",
    alignItems: "center",
    padding: theme.spacing(0, 5),
  },
  ques: {
    marginTop: theme.spacing(5),
    marginBottom: theme.spacing(5),
    position: "relative",
    overflow: "hidden",

    display: "flex",
    flexDirection: "column",
    alignItems: "center",
    fontSize: 24,
    fontFamily: theme.typography.fontFamily,
    fontWeight: theme.typography.fontWeightMedium,
  },
  title: {
    marginBottom: theme.spacing(14),
  },
  number: {
    fontSize: 18,
    fontFamily: theme.typography.fontFamily,
    color: theme.palette.secondary.light,
    fontWeight: theme.typography.fontWeightMedium,
  },
  image: {
    height: 55,
    marginTop: theme.spacing(4),
    marginBottom: theme.spacing(4),
  },
  curvyLines: {
    pointerEvents: "none",
    position: "absolute",
    top: -180,
    opacity: 0.7,
  },
  button: {
    marginTop: theme.spacing(8),
  },
});

function ProductHowItWorks(props) {
  const { classes } = props;
  const [value, setValue] = React.useState(0);

  const handleChange = (event) => {
    setValue(event.target.value);
  };



  return (
    <section className={classes.root}>
      <Container className={classes.container}>
        <img
          src="https://raw.githubusercontent.com/mui-org/material-ui/master/docs/public/static/themes/onepirate/productCurvyLines.png"
          className={classes.curvyLines}
          alt="curvy lines"
        />
        <Typography
          variant="h4"
          marked="center"
          className={classes.title}
          component="h2"
        >
          Answer one of these question to guess your mood
        </Typography>
        <div>
          <Grid container spacing={5}>
            <Grid item xs={12} md={4}>
              <div className={classes.item}>
                <div className={classes.number}>1.</div>
                <img
                  src="https://bit.ly/2VXAGhM"
                  alt="suitcase"
                  className={classes.image}
                />
                <FormControl component="fieldset">
                  <FormLabel
                    className={classes.ques}
                    variant="h1"
                    component="h2"
                  >
                    How much do you feel happy at this moment?
                  </FormLabel>
                  <RadioGroup
                    id="options"
                    aria-label="happy"
                    name="happy"
                    value={value}
                    onChange={handleChange}
                  >
                    <FormControlLabel
                      id="zero"
                      value="0"
                      control={<Radio />}
                      label="0%"
                    />
                    <FormControlLabel
                      id="one"
                      value="50"
                      control={<Radio />}
                      label="50%"
                    />
                    <FormControlLabel
                      id="two"
                      value="100"
                      control={<Radio />}
                      label="100%"
                    />
                  </RadioGroup>
                </FormControl>
              </div>
            </Grid>
            <Grid item xs={12} md={4}>
              <div className={classes.item}>
                <div className={classes.number}>2.</div>
                <img
                  src="https://bit.ly/3gEy9RS"
                  alt="graph"
                  className={classes.image}
                />
                <FormControl component="fieldset">
                  <FormLabel
                    className={classes.ques}
                    variant="h1"
                    component="h2"
                  >
                    Are enjoying drive with your friend?
                  </FormLabel>
                  <RadioGroup
                    id="options"
                    aria-label="happy"
                    name="happy"
                    value={value}
                    onChange={handleChange}
                  >
                    <FormControlLabel
                      id="zero"
                      value="1"
                      control={<Radio />}
                      label="YES"
                    />
                    <FormControlLabel
                      id="one"
                      value="2"
                      control={<Radio />}
                      label="NO"
                    />
                  </RadioGroup>
                </FormControl>
              </div>
            </Grid>
            <Grid item xs={12} md={4}>
              <div className={classes.item}>
                <div className={classes.number}>3.</div>
                <img
                  src="https://upload.wikimedia.org/wikipedia/commons/thumb/f/f1/Heart_coraz%C3%B3n.svg/1200px-Heart_coraz%C3%B3n.svg.png"
                  alt="clock"
                  className={classes.image}
                />
                <FormControl component="fieldset">
                  <FormLabel
                    className={classes.ques}
                    variant="h1"
                    component="h2"
                  >
                    Feeling grateful?
                  </FormLabel>
                  <RadioGroup
                    id="options"
                    aria-label="happy"
                    name="happy"
                    value={value}
                    onChange={handleChange}
                  >
                    <FormControlLabel
                      id="zero"
                      value="3"
                      control={<Radio />}
                      label="YES"
                    />
                    <FormControlLabel
                      id="one"
                      value="4"
                      control={<Radio />}
                      label="NO"
                    />
                  </RadioGroup>
                </FormControl>
              </div>
            </Grid>
          </Grid>
        </div>
        <Button
          color="secondary"
          size="large"
          variant="contained"
          className={classes.button}
          component="a"
          onClick={guess}
        >
          GUESS
        </Button>
      </Container>
    </section>
  );
}

ProductHowItWorks.propTypes = {
  classes: PropTypes.object.isRequired,
};

export default withStyles(styles)(ProductHowItWorks);
