import React from "react";
import { withStyles } from "@material-ui/core/styles";
import Grid from "@material-ui/core/Grid";
import Container from "@material-ui/core/Container";
import Box from '@material-ui/core/Box';
import { borders } from '@material-ui/system';
import CardMedia from "@material-ui/core/CardMedia";
import Typography from '@material-ui/core/Typography';




const styles = (theme) =>  ({
 
    about: {
        marginTop: theme.spacing(10),
        paddingTop: '37%', // 16:9,
        width:"490px",
        height:"200px",
        marginLeft: theme.spacing(108),
      },
    


  image: {
    height:'25%',
    width:"100%",
    marginTop: theme.spacing(40),
    marginBottom: theme.spacing(2),
    border: 1,
    borderRadius:"borderRadius",
    style: { width: '5rem', height: '5rem' },
    borderColor: 'text.primary',

  },
  bak:{
    marginRight: theme.spacing(86),
    margin
  }
//   bord: {
//       borderColor:'secondary'
//   }


});

  



function AboutOur(props) {
    const { classes } = props;


  return (
    <section className={classes.root}>
      
      <CardMedia
      className={classes.about}
      image={"https://i.pinimg.com/564x/07/00/9f/07009fc3a2e0b6259acab3a3dc234589.jpg"}
      title="Contemplative Reptile"
      
    />
        <Typography   className={classes.bak}  variant="h3" marked="center" align="center" component="h2">
    About Our Bakery      </Typography>
        
    </section>
  );
};


  
  export default withStyles(styles)(AboutOur);
