import React from "react";
import PropTypes from "prop-types";
import { withStyles } from "@material-ui/core/styles";
import Button from "@material-ui/core/Button";
import Typography from "@material-ui/core/Typography";
import HomeLayout from "./homePageLayout";

const backgroundImage =
  "https://i.pinimg.com/564x/78/00/e7/7800e73a25e2991c50a515f8d0c02ed4.jpg";

const styles = (theme) => ({
  background: {
    backgroundImage: `url(${backgroundImage})`,
    backgroundColor: "#7fc7d9", // Average color of the background image.
    backgroundPosition: "center",
  },
  button: {
    minWidth: 200,
  },
  h5: {
    marginBottom: theme.spacing(4),
    marginTop: theme.spacing(4),
    [theme.breakpoints.up("sm")]: {
      marginTop: theme.spacing(10),
    },
  },
  more: {
    marginTop: theme.spacing(2),
  },
});

function HomePageHeader(props) {
  const { classes } = props;

  return (
    <HomeLayout backgroundClassName={classes.background}>
      {/* Increase the network loading priority of the background image. */}
      <img
        style={{ display: "none" }}
        src={backgroundImage}
        alt="increase priority"
      />
      <Typography color="inherit" align="center" variant="h2" marked="center">
GoodDay Bakery    
 </Typography>
      <Typography
        color="inherit"
        align="center"
        variant="h4"
        className={classes.h5}
      >
It's the Simple Pleasures in Life    
  </Typography>
      <Button
        // color="primary"
        variant="contained"
        size="large"
        className={classes.button}
        component="a"
        href="/signup"
      >
        Register
      </Button>
    </HomeLayout>
  );
}

HomePageHeader.propTypes = {
  classes: PropTypes.object.isRequired,
};

export default withStyles(styles)(HomePageHeader);
