import React from "react";
import PropTypes from "prop-types";
import { withStyles } from "@material-ui/core/styles";
import Button from "@material-ui/core/Button";
import Typography from "@material-ui/core/Typography";
import HomeLayout from "./homePageLayout";

const backgroundImage =
  "https://static.wixstatic.com/media/898e2587ee2a4e5d86616c3d78c5ccac.jpg";

const styles = (theme) => ({
  background: {
    backgroundImage: `url(${backgroundImage})`,
     backgroundColor: "#fafafa", // Average color of the background image.
    backgroundPosition: "center",
   
  },
  button: {
    minWidth: 200,
  },
  h5: {
    marginBottom: theme.spacing(4),
    marginTop: theme.spacing(4),
    [theme.breakpoints.up("sm")]: {
      marginTop: theme.spacing(10),
    },
  },
  more: {
    marginTop: theme.spacing(2),
  },
});

function HomePageHeader(props) {
  const { classes } = props;

  return (
    <HomeLayout backgroundClassName={classes.background}>
      {/* Increase the network loading priority of the background image. */}
      <img
        style={{ display: "none" }}
        src={backgroundImage}
        alt="increase priority"
        width="50px"
      />
      <Typography color="inherit" align="center" variant="h2" marked="center" colo>
GoodDay Bakery    
 </Typography>
      <Typography
        color="inherit"
        align="center"
        variant="h4"
        className={classes.h5}
      >
It's the Simple Pleasures in Life    
  </Typography>
      <Button
        // color="primary"
        variant="contained"
        size="large"
        className={classes.button}
        component="a"
        href="/signup"
      >
        Register
      </Button>
    </HomeLayout>
  );
}

HomePageHeader.propTypes = {
  classes: PropTypes.object.isRequired,
};

export default withStyles(styles)(HomePageHeader);
